import {useState} from "react";

const Example = () => {
  let displayVal = useState(0);

  return (
    <>
      <input 
        type = "text" 
        onChange={(e)=>{
          displayVal = e.target.value
        }} /> = {displayVal}
    </>
  );
};

export default Example;
